    const [username, setUsername] = useState('익명');

    return (
        <>
            <h1>채팅 프로그램</h1>
            <div id="chat_info">
                <div id="usercount">{usercount}</div>
                <div id="date">{date}</div>
            </div>
            <div id="chat_board">
                <ul id="messages" type="none">
