import React, { useState } from 'react';
import { io } from 'socket.io-client';
import './Chat.css';
// const socket = io('http://113.198.233.57:3000/chat');

const now = new Date();
const now_date = now.getFullYear() + '-' + (now.getMonth() + 1) + '-' + now.getDate();
let user_name = '';
let user_count = 0;
let messages = [];

socket.on('connect', () => {
    console.log('서버에 연결되었습니다.');
    user_name = prompt('이름을 입력하세요.', '익명');
});

socket.on('joinUser', (count) => {
    user_count = count;
});



const Chat = () => {
    const [usercount] = useState(user_count);
    const [date] = useState(now_date);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [username] = useState(user_name);

    socket.on('receiveMessage', (msg) => {
        let username = JSON.stringify(msg.userName);
        let message = JSON.stringify(msg.message);
        let timeStamp = new Date(parseInt(JSON.stringify(msg.timeStamp)));
        const msg_time = timeStamp.getHours() + ':' + timeStamp.getMinutes() + ':' + timeStamp.getSeconds();

        let msg_str = `[${msg_time}] ${username} : ${message}`;
        setMessages([...messages, msg_str]);
    });

    const onSubmit = (e) => {
        e.preventDefault();
        const msginput = document.getElementById('msginput');
        const msg_data = {
            userName: username,
            message: msginput.value,
        };
        socket.emit('sendMessage', msg_data);
        msginput.value = '';
    };

    return (
        <>
            <h1>채팅 프로그램</h1>
            <div id="chat_info">
                <div id="usercount">{usercount}</div>
                <div id="date">{date}</div>
            </div>
            <div id="chat_board">
                <ul id="messages" type="none">
                    {messages.map((msg, index) =>
                        <li key={index + msg}>{msg}</li>)};
                </ul>
            </div>
            <form id="msgform" onSubmit={onSubmit}>
                <input type=" text" id="msginput" autocomplete="off" value={input} onChange={(e) => setInput.target.value} />
                <button id="msgsubmit" type="submit">전송</button>
            </form></>
    )
};

export default Chat;
