import React, { useState } from 'react';
import './Chat.css';

const now = new Date();
const now_date = now.getFullYear() + '-' + (now.getMonth() + 1) + '-' + now.getDate();
let user_name = '';
let user_count = 0;
let messages = [];


const Chat = () => {
    const [usercount] = useState(user_count);
    const [date] = useState(now_date);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [username] = useState(user_name);



    return (
        <>
            <h1>채팅 프로그램</h1>
            <div id="chat_info">
                <div id="usercount">{usercount}</div>
                <div id="date">{date}</div>
            </div>
            <div id="chat_board">
                <ul id="messages" type="none">
                    {messages.map((msg, index) =>
                        <li key={index + msg}>{msg}</li>)};
                </ul>
            </div>
            <form id="msgform" >
                <input type=" text" id="msginput" autocomplete="off" />
                <button id="msgsubmit" type="submit">전송</button>
            </form></>
    )
};

export default Chat;
